// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import {ERC20StakingPointsRewardsLinearPool} from "./ERC20StakingPointsRewardsLinearPool.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IForwarderRegistry} from "@animoca/ethereum-contracts/contracts/metatx/interfaces/IForwarderRegistry.sol";
import {IPoints} from "./../points/interface/IPoints.sol";

contract ERC20StakingPointsRewardsLimitedLinearPool is ERC20StakingPointsRewardsLinearPool {
    address public immutable CLAIM_CONTRACT;

    error InvalidTransferOperator(address operator);
    error OnlyReceiverInterface();

    constructor(
        address claimContract,
        IERC20 stakingToken,
        IPoints pointsContract,
        bytes32 depositReasonCode,
        IForwarderRegistry forwarderRegistry
    ) ERC20StakingPointsRewardsLinearPool(stakingToken, pointsContract, depositReasonCode, forwarderRegistry) {
        CLAIM_CONTRACT = claimContract;
    }

    function onERC20Received(address operator, address, uint256 value, bytes calldata data) external virtual override returns (bytes4) {
        require(msg.sender == address(STAKING_TOKEN), InvalidToken());
        require(operator == CLAIM_CONTRACT, InvalidTransferOperator(operator));
        address staker = abi.decode(data, (address));
        _stake(staker, abi.encode(value));
        return this.onERC20Received.selector;
    }

    function stake(bytes calldata) public payable virtual override {
        revert OnlyReceiverInterface();
    }

    function _computeStake(address, bytes memory stakeData) internal virtual override returns (uint256 stakePoints) {
        // The tokens were received via the receiver interface, so we don't need to transfer them.
        stakePoints = abi.decode(stakeData, (uint256));
    }
}

// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import {LinearPool} from "@animoca/ethereum-contracts/contracts/staking/linear/LinearPool.sol";
import {ERC20StakingLinearPool} from "@animoca/ethereum-contracts/contracts/staking/linear/stake/ERC20StakingLinearPool.sol";
import {LinearPool_PointsRewards} from "./reward/LinearPool_PointsRewards.sol";
import {ERC20Receiver} from "@animoca/ethereum-contracts/contracts/token/ERC20/ERC20Receiver.sol";
import {IERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {IForwarderRegistry} from "@animoca/ethereum-contracts/contracts/metatx/interfaces/IForwarderRegistry.sol";
import {IPoints} from "./../points/interface/IPoints.sol";

contract ERC20StakingPointsRewardsLinearPool is ERC20StakingLinearPool, LinearPool_PointsRewards {
    constructor(
        IERC20 stakingToken,
        IPoints pointsContract,
        bytes32 depositReasonCode,
        IForwarderRegistry forwarderRegistry
    ) ERC20StakingLinearPool(stakingToken, forwarderRegistry) LinearPool_PointsRewards(pointsContract, depositReasonCode) {}

    function _computeClaim(
        address staker,
        uint256 reward
    ) internal virtual override(LinearPool, LinearPool_PointsRewards) returns (bytes memory claimData) {
        return LinearPool_PointsRewards._computeClaim(staker, reward);
    }

    function _computeAddReward(address rewarder, uint256 reward, uint256 dust) internal virtual override(LinearPool, LinearPool_PointsRewards) {
        LinearPool_PointsRewards._computeAddReward(rewarder, reward, dust);
    }
}

contracts/staking/reward/LinearPool_Poin.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.28;

import {IPoints} from "./../../points/interface/IPoints.sol";

// solhint-disable-next-line contract-name-capwords
abstract contract LinearPool_PointsRewards {
    IPoints public immutable POINTS_CONTRACT;
    bytes32 public immutable DEPOSIT_REASON_CODE;

    constructor(IPoints pointsContract, bytes32 depositReasonCode) {
        POINTS_CONTRACT = pointsContract;
        DEPOSIT_REASON_CODE = depositReasonCode;
    }

    function _computeClaim(address sender, uint256 reward) internal virtual returns (bytes memory claimData) {
        claimData = abi.encode(reward);
        POINTS_CONTRACT.deposit(sender, reward, DEPOSIT_REASON_CODE);
    }

    function _computeAddReward(address sender, uint256 reward, uint256 dust) internal virtual {}
}

