pragma solidity 0.8.22;

import {IERC1155} from "@animoca/ethereum-contracts/contracts/token/ERC1155/interfaces/IERC1155.sol";
import {ERC1155TokenReceiver} from "@animoca/ethereum-contracts/contracts/token/ERC1155/ERC1155TokenReceiver.sol";
import {IERC1155Burnable} from "@animoca/ethereum-contracts/contracts/token/ERC1155/interfaces/IERC1155Burnable.sol";
import {ForwarderRegistryContext} from "@animoca/ethereum-contracts/contracts/metatx/ForwarderRegistryContext.sol";
import {ContractOwnership} from "@animoca/ethereum-contracts/contracts/access/ContractOwnership.sol";
import {ContractOwnershipStorage} from "@animoca/ethereum-contracts/contracts/access/libraries/ContractOwnershipStorage.sol";
import {ForwarderRegistryContextBase} from "@animoca/ethereum-contracts/contracts/metatx/base/ForwarderRegistryContextBase.sol";
import {IForwarderRegistry} from "@animoca/ethereum-contracts/contracts/metatx/interfaces/IForwarderRegistry.sol";
import {Context} from "@openzeppelin/contracts/utils/Context.sol";

/**
 * @title Anichess ERC1155 Merkle Claim Contract
 * @dev This contract allows users to claim rewards based on a Merkle proof, which verifies that they are
 * @dev entitled to the rewards without revealing the entire list of recipients.
 */
contract AnichessOrbsBurnPool is ForwarderRegistryContext, ERC1155TokenReceiver, ContractOwnership {
    using ContractOwnershipStorage for ContractOwnershipStorage.Layout;

    uint256 public immutable INITIAL_TIME;
    uint256 public immutable CYCLE_DURATION; // time of each cycle
    uint256 public immutable MAX_CYCLE;

    uint256[] public tiers; // ascending order
    uint256[] public tierRewards;

    mapping(uint256 => uint256) totalAshByCycle;
    mapping(uint256 => mapping(address => uint256)) public userAshByCycle;
    // mapping(uint256 => mapping(address => bool)) public claimStatusByCycle;

    /// @notice The IERC1155Burnable erc1155 contracts.
    mapping(address => bool) public tokens;
    /// @notice The token weights.
    mapping(address => mapping(uint256 => uint256)) tokenWeights;

    /// @notice Event emitted when tokens are burnt.
    event TokensBurnt(
        address indexed burner,
        address indexed token,
        uint256 cycle,
        uint256 timestamp,
        uint256[] ids,
        uint256[] values,
        uint256 totalAsh
    );

    event Claimed(address indexed claimer, uint256 indexed cycle, uint256 amount);

    /// @notice Error thrown when the token ID is invalid.
    error InvalidTokenId(address token, uint256 tokenId);

    /// @notice Error thrown when the array lengths are inconsistent.
    error InconsistentArrays();

    /// @notice Error thrown when the tiers are incorrect.
    error IncorrectTiers();

    /// @notice Error thrown when the token is not approved.
    error InvalidToken();

    /// @notice Error throw when the function is not implemented.
    error NotImplemented();

    /// @notice Error thrown when the cycle is invalid.
    error InvalidCycle(uint256 cycle);

    // error AlreadyClaimed();

    /**
     * @notice Constructor for the ERC1155TokenBurn contract.
     * @param initialTime The initial time of the contract.
     * @param cycleDuration The duration of each cycle.
     * @param maxCycle The maximum cycle.
     * @param _tokens The list of IERC1155Burnable tokens to be approved.
     * @param tokenIds The list of token IDs for each token.
     * @param weights The list of weights for each token ID.
     * @param _tiers The list of tiers.
     * @param _tierRewards The list of rewards for each tier.
     * @param forwarderRegistry The forwarder registry contract.
     */
    constructor(
        uint256 initialTime,
        uint256 cycleDuration,
        uint256 maxCycle,
        IERC1155Burnable[] memory _tokens,
        uint256[][] memory tokenIds,
        uint256[][] memory weights,
        uint256[] memory _tiers,
        uint256[] memory _tierRewards,
        IForwarderRegistry forwarderRegistry
    ) ForwarderRegistryContext(forwarderRegistry) ContractOwnership(msg.sender) {
        INITIAL_TIME = initialTime;
        CYCLE_DURATION = cycleDuration;
        MAX_CYCLE = maxCycle;

        _setApprovedTokens(_tokens, tokenIds, weights);
        _setTiers(_tiers, _tierRewards);
    }

    /**
     * @notice Set the approved tokens.
     * @param _tokens The list of IERC1155Burnable tokens to be approved.
     * @param tokenIds The list of token IDs for each token.
     * @param weights The list of weights for each token ID.
     */
    function _setApprovedTokens(IERC1155Burnable[] memory _tokens, uint256[][] memory tokenIds, uint256[][] memory weights) internal {
        if (_tokens.length != tokenIds.length || _tokens.length != weights.length) {
            revert InconsistentArrays();
        }

        // set the approved tokens
        for (uint256 i = 0; i < _tokens.length; i++) {
            tokens[address(_tokens[i])] = true;

            if (tokenIds[i].length != weights[i].length) {
                revert InconsistentArrays();
            }

            for (uint256 j = 0; j < tokenIds[i].length; j++) {
                tokenWeights[address(_tokens[i])][tokenIds[i][j]] = weights[i][j];
            }
        }
    }

    /**
     * @notice Set the tiers and rewards.
     * @param _tiers The list of tiers.
     * @param _tierRewards The list of rewards for each tier.
     */
    function _setTiers(uint256[] memory _tiers, uint256[] memory _tierRewards) internal {
        if (_tiers.length != _tierRewards.length) {
            revert InconsistentArrays();
        }

        if (_tiers.length == 0) {
            revert IncorrectTiers();
        }

        // check if tiers are in increasing order
        for (uint256 i = 1; i < _tiers.length; i++) {
            if (_tiers[i] < _tiers[i - 1]) {
                revert IncorrectTiers();
            }
        }
        tiers = _tiers;
        tierRewards = _tierRewards;
    }

    /// @inheritdoc ForwarderRegistryContextBase
    function _msgSender() internal view virtual override(Context, ForwarderRegistryContextBase) returns (address) {
        return ForwarderRegistryContextBase._msgSender();
    }

    /// @inheritdoc ForwarderRegistryContextBase
    function _msgData() internal view virtual override(Context, ForwarderRegistryContextBase) returns (bytes calldata) {
        return ForwarderRegistryContextBase._msgData();
    }

    function _currentCycle() internal view returns (uint256) {
        return (block.timestamp - INITIAL_TIME) / CYCLE_DURATION;
    }


    function getCycleReward(uint256 cycle) public view returns (uint256) {
        if (cycle > _currentCycle() || cycle > MAX_CYCLE) {
            revert InvalidCycle(cycle);
        }

        uint256 prevTotalAsh = cycle == 0 ? 0 : totalAshByCycle[cycle - 1];

        for (uint256 i = 1; i <= tiers.length; i++) {
            if (prevTotalAsh < tiers[i]) {
                return tierRewards[i - 1];
            }
        }
        return tierRewards[tiers.length - 1];
    }

    function getUserReward(uint256 cycle, address wallet) external view returns (uint256) {
        if (cycle > _currentCycle() || cycle > MAX_CYCLE) {
            revert InvalidCycle(cycle);
        }
        uint256 cycleReward = getCycleReward(cycle);
        uint256 userAsh = userAshByCycle[cycle][wallet];
        return (userAsh * cycleReward) / totalAshByCycle[cycle];
    }

    function onERC1155Received(address, address, uint256, uint256, bytes calldata) external pure override returns (bytes4) {
        revert NotImplemented();
    }

    function onERC1155BatchReceived(
        address,
        address from,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata
    ) external override returns (bytes4) {
        if (!tokens[msg.sender]) {
            revert InvalidToken();
        }

        uint256 currentCycle = _currentCycle();
        if (currentCycle > MAX_CYCLE) {
            revert InvalidCycle(currentCycle);
        }

        uint256 totalAsh = 0;

        // calculate total burned
        for (uint256 i = 0; i < ids.length; i++) {
            uint256 weight = tokenWeights[msg.sender][ids[i]];
            if (weight == 0) {
                revert InvalidTokenId(msg.sender, ids[i]);
            }
            totalAsh += (values[i] * weight);
        }

        // update the user status
        userAshByCycle[currentCycle][from] += totalAsh;
        // update the pool status
        totalAshByCycle[currentCycle] += totalAsh;

        IERC1155Burnable(msg.sender).batchBurnFrom(from, ids, values);
        emit TokensBurnt(from, msg.sender, currentCycle, block.timestamp, ids, values, totalAsh);

        return this.onERC1155BatchReceived.selector;
    }

    // function setApprovedTokens(IERC1155Burnable[] calldata tokens, bool[] calldata enabled) external {
    //     ContractOwnershipStorage.layout().enforceIsContractOwner(_msgSender());
    //     if (tokens.length != enabled.length) {
    //         revert InconsistentArrays();
    //     }
    //     for (uint256 i = 0; i < tokens.length; i++) {
    //         tokens[address(tokens[i])] = enabled[i];
    //     }
    // }

    // function setTokensWeight(address[] calldata tokens, uint256[][] calldata tokenIds, uint256[][] calldata weights) external {
    //     ContractOwnershipStorage.layout().enforceIsContractOwner(_msgSender());

    //     if (tokens.length != tokenIds.length || tokens.length != weights.length) {
    //         revert InconsistentArrays();
    //     }

    //     for (uint256 i = 0; i < tokens.length; i++) {
    //         if (!tokens[tokens[i]]) {
    //             revert InvalidToken();
    //         }
    //         if (tokenIds[i].length != weights[i].length) {
    //             revert InconsistentArrays();
    //         }

    //         for (uint256 j = 0; j < tokenIds[i].length; j++) {
    //             tokenWeights[tokens[i]][tokenIds[i][j]] = weights[i][j];
    //         }
    //     }
    // }

    // function claim(uint256 cycle) external {
    //     uint256 currentCycle = _currentCycle();
    //     if (cycle >= currentCycle) {
    //         revert InvalidCycle(cycle);
    //     }
    //     if (claimStatusByCycle[cycle][_msgSender()]) {
    //         revert AlreadyClaimed();
    //     }

    //     // calcualte the claim amount
    //     PoolStatus memory poolStatus = poolStatusByCycle[cycle];
    //     uint256 cycleReward = getCycleReward(cycle);
    //     uint256 walletBurned = userAshByCycle[cycle][_msgSender()];
    //     uint256 claimAmount = (walletBurned * cycleReward) / poolStatus.totalAsh;

    //     // update the status
    //     claimStatusByCycle[cycle][_msgSender()] = true;
    //     poolStatusByCycle[cycle].totalClaimed = poolStatus.totalClaimed + claimAmount;

    //     emit Claimed(_msgSender(), cycle, claimAmount);
    // }
}
